var extension_times = 3;

const observer = new MutationObserver(list => {
    obfuscate_text();
});

const parental_twitting = setInterval(parental_twitting_dom_loaded, 300); // chiama la funzione parental_twitting_dom_loaded ogni 3 decimi di secondo fino a che non viene cancellato "interval"

const injectCSS = css => {
    let el = document.createElement('style');
    el.type = 'text/css';
    el.innerText = css;
    document.head.appendChild(el);
    return el;
};

injectCSS(".parental_twitter_obfuscate_div {position:relative; color: transparent; text-shadow: 0 0 4px #000;}"); // classe di offuscamento

function parental_twitting_dom_loaded() {
    var tweet_container = document.querySelector('section.css-1dbjc4n[role="region"]'); // recupera l'elemento section che contiene i tweet
    if (tweet_container) { // se la pagina contiene già l'elemento section dei tweet
        clearInterval(parental_twitting);// cancello la "interval" che controlla la presenza del dom (e del tag section)

        obfuscate_text();//chiamata alla funzione che recupera i tweet
        observer.observe(document.querySelector('section.css-1dbjc4n[role="region"]'), { // observe controlla se dentro a section sono arrivati nuovi tweet (se ci sono cambiamenti dentro alla section chiama obfuscate_text che gli abbiamo detto alla riga 4)
            attributes: false,
            childList: true,// se dentro alla section cambia la lista dei figli
            subtree: true// se dentro alla section cambia l'alberatura
        });
    }
}

/*
* Funzione che cicla tutti i tweet (che hanno l'attributo data-testid="tweetText"]) e per ogni tweet chiama la funzione mlCall
* */
function obfuscate_text() {
    spans = document.querySelectorAll('[data-testid="tweetText"]'); //recupera tutti i tweet
    let arr_tweet = [];
    for (var i = 0; i < spans.length; i++) {//per ogni tweet
        if (i > 5) {//se sono più di 5 tweet non ne controllo altri
            break;
        }
        arr_tweet.push(spans[i]);
    }

    if (arr_tweet.length > 0) {
        arr_tweet.forEach(mlCall);

        if (extension_times <= 0) { // if creato per effettuare il controllo dell'observer solo 3 volte.
            observer.disconnect();
        } else {
            extension_times--;
        }
    }
}

/*
* Funzione che chiama l'API per il ML sul testo di "element" passato come argomento. La chiamata alla API è effettuata in ajax
* */
function mlCall(element) {
    let obfuscate_test_div = element.parentNode.querySelector("div.parental_twitter_obfuscate_div"); // offusca subito i tweet
    if(!obfuscate_test_div) {
        element.innerHTML = "<div class='parental_twitter_obfuscate_div'>" + element.innerText + "</div>";
    }

    let xmlhttp = new XMLHttpRequest;

    xmlhttp.open("POST", "http://127.0.0.1:3000");
    xmlhttp.setRequestHeader("Content-Type", "application/json");
    xmlhttp.onload = function (e) {
        if (xmlhttp.readyState === 4) {
            if (xmlhttp.status === 200) {//se l'API ritorna 200 OK
                try {
                    let json_arr = JSON.parse(xmlhttp.response);//recupera la risposta della API
                    if (json_arr[json_arr.length - 1]["results"][0]["match"]) { // recupero ultimo elemento dell'oggetto response (perchè contiene un bool, indicante la tossicità o meno)

                        let parent_div = null;//conterrà il div contenitore del tweet
                        let parent_element = element.parentNode;
                        while (parent_element && parent_element.nodeName.toLowerCase() != 'body') {
                            if (parent_element.getAttribute("data-testid") && parent_element.getAttribute("data-testid") == "cellInnerDiv") {
                                parent_div = parent_element;
                                break;
                            }

                            parent_element = parent_element.parentNode;
                        }

                        let tox_child = parent_div.getElementsByClassName("tox_img");

                        if (parent_div && tox_child.length == 0) { // se non ha un immagine 'occhio', allora aggiungo
                            const eye = document.createElement("img");
                            eye.src = "data:image/png;base64,";
                            eye.style.cssText += 'position: absolute; right: 9%;top: 13%;height: 1.2em;cursor:pointer;';
                            eye.classList.add("tox_img");
                            eye.addEventListener("click", function () {
                                toggleObfuscation(this);
                            });
                            parent_div.appendChild(eye);
                        }
                    } else {
                        if (obfuscate_test_div){
                        obfuscate_test_div.classList.add('parental_twitter_nobfuscate_div');
                        obfuscate_test_div.classList.remove('parental_twitter_obfuscate_div');
                        }
                    }
                } catch (e) {
                    console.log(e);
                }
            } else {

            }
        }
    };
    xmlhttp.send(JSON.stringify([element.innerText]));
}

function toggleObfuscation(el) {
    let obfuscate_test_div = el.parentNode.querySelector("div.parental_twitter_obfuscate_div");
    if (obfuscate_test_div) {
        el.src = "data:image/png;base64,";
        obfuscate_test_div.classList.add('parental_twitter_nobfuscate_div');
        obfuscate_test_div.classList.remove('parental_twitter_obfuscate_div');
    } else {
        let nobfuscate_test_div = el.parentNode.querySelector("div.parental_twitter_nobfuscate_div");
        if (nobfuscate_test_div) {
            el.src = "data:image/png;base64,";
            nobfuscate_test_div.classList.remove('parental_twitter_nobfuscate_div');
            nobfuscate_test_div.classList.add('parental_twitter_obfuscate_div');
        }
    }
}